import React, { useState, useEffect } from "react";
import { Box, Grid, ThemeProvider, Typography, CircularProgress } from "@material-ui/core";
import theme from "./theme/theme";
import Header from "./components/Header"
import SearchBar from "./components/SearchBar";
import JobCard from "./components/Job/JobCard";
import { firestore, app } from "./firebase/config";
import NewJobModal from "./components/Job/NewJobModal";


// import jobData from './dummyData'; // initialally used dummy data

export default () =>{
    const [jobs, setJobs] = useState([]);
    
    const [loading, setLoading] = useState(true);
     
    // create state modal
    const [newJobModal, setNewJobModal] = useState(false);




    const fetchJobs = async () =>{
      const req = await firestore // firestore is a collection of all the jobs
      .collection("jobs")
      .orderBy("postedOn", "desc")
      .get();
      const tempJobs = req.docs.map((job) =>({ ...job.data(), id: job.id, postedOn: job.data().postedOn.toDate() }));
      
      setJobs(tempJobs);  // set the jobs to the tempJobs
      setLoading(false);
    };

  // post jobs to firestore database
    const postJob = async jobDetails =>{
      await firestore.collection("jobs").add({
        ...jobDetails,
        postedOn: app.firestore.FieldValue.serverTimestamp()


      });
      fetchJobs()// fetch jobs again after posting

    };

    useEffect(() => {
        fetchJobs();
    } , [])

  return (
    <ThemeProvider theme={theme}>
      
        <Header openNewJobModal ={() => setNewJobModal(true)} />
        <NewJobModal closeModal={() => setNewJobModal(false)} 
        newJobModal={newJobModal} postJob={postJob} />

        <Box mb={5}>
        <Grid container justify="center">
          <Grid item xs={10}>
            <SearchBar />

            {
              loading ? 
              <Box display="flex" justifyContent="center" mt={2}>
              
              <CircularProgress color="secondary" />
              </Box>

              : 
              jobs.map(job => (
                <JobCard key={job.id} {...job} />)
                )

            }

           

              
            </Grid>
        </Grid>
        </Box>
    </ThemeProvider>
  );
}
