import React from 'react';
import { Box, Button, Select, MenuItem, makeStyles} from '@material-ui/core'
import { red } from '@material-ui/core/colors';


const useStyles = makeStyles({ 
    wrapper: {
        backgroundColor: '#f5f5f5',
        display: 'flex',
        boxShadow: '0px 2px 4px -1px rgba(0,0,0,0.2), 0px 4px 5px 0px rgba(0,0,0,0.14), 0px 1px 10px 0px rgba(0,0,0,0.12)',
        borderRadius: '5px',
        "& > *":{
            flex: 1, 
            height: "45px",
            margin: "8px"
        },
        
    },

}
)

export default (props) =>{
    const classes = useStyles(); // useStyles() returns an object with all the classes

    return(
    <Box p={2} mt={-5} mb={5} className={classes.wrapper}>
        <Select disableUnderline variant="filled" defaultValue="Full time">
            <MenuItem value="Full time">Full time </MenuItem>
            <MenuItem value="Part time">Part time</MenuItem>
            <MenuItem value="Contract">Contract</MenuItem>

        </Select>

        <Select disableUnderline variant="filled" defaultValue="Remote">
            <MenuItem value="Remote">Remote</MenuItem>
            <MenuItem value="In-office">In-office</MenuItem>
            

        </Select>
        <Button variant="contained" color="primary" disableElevation>
            Search</Button>


    </Box>

    );
}
